	add("ecodisaster", "{\"term\":\"ecodisaster\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|disaster|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An ecological disaster\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecoefficiency", "{\"term\":\"ecoefficiency\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|efficiency|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"efficient, non-wasteful use of environmental resources\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecofascism", "{\"term\":\"ecofascism\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|fascism|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The combination of fascist politics with support for ecological concerns\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Aggressive environmental activism\", \"priority\":2}]}, \"synonyms\":{}}");

