	add("ecofreak", "{\"term\":\"ecofreak\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|freak|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A person with a passion for protecting the natural environment; an ecological activist\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1973\u0027\u0027, \u0027Daedalus: Proceedings of the American Academy of Arts and Sciences\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The initial result of the rational \u0027\u0027ecofreak\u0027\u0027 policies would be that the level of measured income would fall, or at least rise less rapidly\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"1999\u0027\u0027, Fred H Knelman, \u0027Every Life is a Story\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"The corporate world has responded to a perceived threat to the economic order by casting the environmentalist as un-American, as an \u0027\u0027ecofreak\", \"priority\":5}]}, \"synonyms\":{}}");

	add("ecogeography", "{\"term\":\"ecogeography\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|geography|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The study of environmental effects on the distribution of living thing\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecohorror", "{\"term\":\"ecohorror\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|horror|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A horror genre dealing with things that go wrong in the natural environment\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecohotel", "{\"term\":\"ecohotel\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|hotel|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A hotel that is designed and run so as to have minimal environmental impact\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2011\u0027\u0027, Michael Krondl, \u0027Sweet Invention: A History of Dessert\u0027 (page 302\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"There are \u0027\u0027ecohotels\u0027\u0027 that promise zero environmental impact. There is a vibrant arts scene, and cutting-edge restaurants dish up the most up-to-date global-inflected cuisine\", \"priority\":3}]}, \"synonyms\":{}}");

	add("ecolabel", "{\"term\":\"ecolabel\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|label|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A label on a product that states that it has been produced in an environmentally friendly manner\", \"priority\":1}]}, \"synonyms\":{}}");

