	add("ecolinguistics", "{\"term\":\"ecolinguistics\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|linguistics|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A paradigm of linguistic research which emerged in the 1990s and takes into account not only the social context in which language is embedded, but also the ecological context in which societies are embedded\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecologist", "{\"term\":\"ecologist\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{confix|eco|logist|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A scholar of ecology\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecology", "{\"term\":\"ecology\", \"etymology\":{\"influencers\":[], \"languages\":[\"German\", \"English\", \"ancient greek (to 1453)\"], \"text\":\"From German \u0027ökologie\u0027 from Ancient greek (to 1453) \u0027οἶκος\u0027 (house) + \u0027-λογία\u0027 (study of)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The branch of biology dealing with the relationships of organisms with their environment and with each other\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1949\u0027\u0027 - Bruce Kiskaddon, George R. Stewart Earth Abides\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"As a graduate student, he was working on a thesis: \u0027The \u0027\u0027Ecology\u0027\u0027 of the Black Creek Area\u0027. He had to investigate the relationships, past and present, of men and plants and animals in this region\", \"priority\":3}]}, \"synonyms\":{}}");

	add("ecomap", "{\"term\":\"ecomap\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|map|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A diagram that shows all of the systems at play in an individual\u0027s life, used in social work\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecomigrant", "{\"term\":\"ecomigrant\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|migrant|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One who migrates for economic or ecological reasons\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecomigration", "{\"term\":\"ecomigration\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|migration|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The migration of populations for economic or ecological reasons, often as a result of the deterioration of land quality or a shortage of water\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecomodernism", "{\"term\":\"ecomodernism\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|modernism|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A movement based upon the assumption that ecofriendliness is compatible with economic prosperity\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecomuseum", "{\"term\":\"ecomuseum\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|museum|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A museum, often consisting of replica buildings in an outdoor setting, that shows the heritage of a particular locality or community\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecomusicology", "{\"term\":\"ecomusicology\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|musicology|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The study of music, culture, and nature, related to ecology and the natural environment. A mixture of the fields of ecocriticism and musicology\", \"priority\":1}]}, \"synonyms\":{}}");

	add("econazi", "{\"term\":\"econazi\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|nazi|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An environmentalist, especially a hardline one\", \"priority\":1}]}, \"synonyms\":{}}");

	add("econobabble", "{\"term\":\"econobabble\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|econo|babble|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Excessively obscure economic jargon\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Once you cut through all the \u0027\u0027econobabble\u0027\u0027, it says we\u0027re headed for a recession\", \"priority\":2}]}, \"synonyms\":{}}");

	add("economics", "{\"term\":\"economics\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From \u0027economy\u0027 from Latin \u0027oeconomia\u0027 from Ancient greek (to 1453) \u0027οἰκονομία\u0027 (management of a household, administration) from \u0027οἶκος\u0027 (house) + \u0027νόμος\u0027 (law)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The study of resource allocation, distribution and consumption; of capital and investment; and of management of the factors of production\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"title\u003d[http://www.economist.com/news/finance-and-economics/21582498-america-has-changed-way-it-measures-gdp-boundary-problems Boundary passage\u003d\u0027\u0027Economics\u0027\u0027 is a messy discipline: too fluid to be a science, too rigorous to be an art. Perhaps it is fitting that economists’ most-used metric, gross domestic product (GDP), is a tangle too\", \"priority\":2}]}, \"synonyms\":{}}");

	add("economism", "{\"term\":\"economism\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{suffix|economy|ism|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The belief that economic causes or factors have dominance over all others\", \"priority\":1}]}, \"synonyms\":{}}");

	add("economization", "{\"term\":\"economization\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|economize|ation|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The act or practice of using resources to the best effect\", \"priority\":1}]}, \"synonyms\":{}}");

	add("economizer", "{\"term\":\"economizer\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|economize|er|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A person who avoids wast\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A heat exchange device in a boiler that improves efficiency and saves fuel\", \"priority\":2}]}, \"synonyms\":{}}");

	add("economy", "{\"term\":\"economy\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Latin {{m|la|oeconomia}}, from Ancient greek (to 1453) {{m|grc|οἰκονομία||management of a household, administration}}, from {{m|grc|οἶκος||house}} + {{m|grc|νόμος||law}} (surface analysis {{confix|eco|nomy|lang\u003den}}). The first recorded sense of the word \u0027\u0027economy\u0027\u0027, found in a work possibly composed in 1440, is “the management of economic affairs”, in this case, of a monastery.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Effective management of the resources of a community or system\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Collective focus of the study of money, currency and trade, and the efficient use of resources\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Frugal use of resources\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"Jonathan Swif\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"I have no other notion of \u0027\u0027economy\u0027\u0027 than that it is the parent to liberty and ease\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"The system of production and distribution and consumption. The overall measure of a currency system; as the national economy\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"title\u003d[http://www.economist.com/news/finance-and-economics/21584351-how-can-emerging-economies-protect-themselves-rich-worlds-monetary Horns of a passage\u003dAn \u0027\u0027economy\u0027\u0027 open to free movement of capital can keep a fixed exchange rate, for example, only by subjugating monetary-policy goals to its defence—by raising interest rates sharply, say, when capital outflows put downward pressure on the currency. Yet the trilemma also implies that an economy can enjoy both free capital flows and an independent monetary policy, so long as it gives up worrying about its exchange rate\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"The method of divine government of the world\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"Management of one’s\", \"priority\":9}]}, \"synonyms\":{}}");

	add("ecophysiology", "{\"term\":\"ecophysiology\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|physiology|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The study of the relationships between, and adaptation of, the physiology of an organism and its environment\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecopoem", "{\"term\":\"ecopoem\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|poem|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A poem with a strong ecological message or emphasis\", \"priority\":1}]}, \"synonyms\":{}}");

	add("ecopoet", "{\"term\":\"ecopoet\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|poet|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A writer of ecopoetry\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1996\u0027\u0027, California State Coastal Conservancy, \u0027California coast and ocean, volumes 12-14\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Except for a few \u0027\u0027ecopoets\u0027\u0027 and visionaries, nearly everyone saw open acreage around San Francisco Bay as prime real estate\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2002\u0027\u0027, David W Gilcrest, \u0027Greening the lyre: environmental poetics and ethics\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"For \u0027\u0027ecopoets\u0027\u0027 language is an instrument that the poet continually refurbishes to articulate his originary experience in nature\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"2007\u0027\u0027, Kenneth R Cervelli, \u0027Dorothy Wordsworth\u0027s ecology\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"Dorothy\u0027s journal entry beautifully captures this new, holistic perspective as a process, offering us a glimpse of not just a poet but an \u0027\u0027ecopoet\u0027\u0027 at work\", \"priority\":7}]}, \"synonyms\":{}}");

	add("ecopoetry", "{\"term\":\"ecopoetry\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|eco|poetry|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Poetry with a strong ecological message or emphasis\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2002\u0027\u0027, Julian Wolfreys, \u0027Introducing criticism at the 21st century‎\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The best \u0027\u0027ecopoetry\u0027\u0027, in Bate\u0027s view, is not overtly political, let alone propagandistic\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2000\u0027\u0027, Jonathan Bate, \u0027The song of the Earth‎\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"Ecopoetry\u0027\u0027 is not synonymous with writing that is pragmatically green: a manifesto for ecological correctness will not be poetic because its language is bound to be instrumental, to address questions of doing rather than to \u0027present\u0027 the experience of dwelling\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"2005\u0027\u0027, J Scott Bryson, \u0027The west side of any mountain: place, space, and \u0027\u0027ecopoetry\", \"priority\":6}]}, \"synonyms\":{}}");

	add("ecopoiesis", "{\"term\":\"ecopoiesis\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"Coined by w:Robert Haynes|Robert Haynes in 1990, from \u0027\u0027 + \u0027\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The artificial creation of a sustainable ecosystem on a lifeless planet\", \"priority\":1}]}, \"synonyms\":{}}");

