	add("ecosystem", "{\"term\":\"ecosystem\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A system formed by an ecological community and its environment that functions as a unit\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The interconnectedness of organisms (plants, animals, microbes) with each other and their environment\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A set of interconnected products and services\", \"priority\":3}]}, \"synonyms\":{}}");

