	add("perlection", "{\"term\":\"perlection\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"Latin\"], \"text\":\"From Latin \u0027perlēctiō\u0027 (the act of reading through) from \u0027perlegō\u0027 (read through) from \u0027per\u0027 (through) + \u0027legō\u0027 (read)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"the reading through of something\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1923\u0027\u0027, Edward Powys Mathers, translator, \u0027The Thousand Nights and One Night\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"When the \u0027\u0027perlection\u0027\u0027 of the Koran had been accomplished, the cloth was laid and all manner of meats and necessary things were brought\", \"priority\":3}]}, \"synonyms\":{}}");

