	add("permission", "{\"term\":\"permission\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"Latin\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027permission\u0027 from Latin \u0027permissio\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"authorisation; consent (especially formal consent from someone in authority\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Sire, do I have your \u0027\u0027permission\u0027\u0027 to execute this traitor\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The act of permitting\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"flags or access control lists pertaining to a file that dictate who can access it, and how\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"I used the \u0026quot;chmod\u0026quot; command to change the file\u0027s \u0027\u0027permission\", \"priority\":5}]}, \"synonyms\":{}}");

	add("permuter", "{\"term\":\"permuter\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|permute|er|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One who permutes\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perniciousness", "{\"term\":\"perniciousness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|pernicious|ness|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The condition of being pernicious; destructiveness\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peroration", "{\"term\":\"peroration\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Latin\"], \"text\":\"Adopted from the Latin \u0027perōrō\u0027 (i speak at length, i conclude a speech) {{suffix||ation|t2\u003daction (nominalizer)|lang\u003den}}, from Latin \u0027-ātiō\u0027 from \u0027per-\u0027 (very) + \u0027oro\u0027 (English \u0027orate\u0027 .\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The concluding section of a discourse, either written or oral, in which the orator or writer sums up and commends his topic to his audience, particularly as used in the technical sense of a component of ancient Roman oratorical delivery\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1946\u0027\u0027, \u0027History of Western Philosophy\u0027, I.20\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"This passage is virtually the \u0027\u0027peroration\u0027\u0027 of the \u0027Ethics\u0027; the few paragraphs that follow are concerned with the transition to politics\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"1978\u0027\u0027, \u0027Livia\u0027, Faber \u0026amp; Faber 1992 (\u0027Avignon Quintet\u0027), page 480\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"The young man achieved perfect timing, for the last word of his \u0027\u0027peroration\u0027\u0027 coincided with the muffled clap of the doors closing, after having launched the coffin onto the rails of a subterranean railway\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"A discourse or rhetorical argument in general\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"w:William William Shakespeare, \u0027King Henry VI Part II\u0027, act 1, scene 1\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"Nephew, what means this passionate discourse\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"This \u0027\u0027peroration\u0027\u0027 with such circumstance\", \"priority\":9}]}, \"synonyms\":{}}");

	add("perpetrator", "{\"term\":\"perpetrator\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One who perpetrates; especially, one who commits an offence or crime\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perpetuator", "{\"term\":\"perpetuator\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|perpetuate|or|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One who perpetuates\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2005\u0027\u0027, Robert Spencer, \u0027The myth of Islamic tolerance\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Thus, by the end of the twentieth century, Christian scholars of Islam had become the unwitting guardians and \u0027\u0027perpetuators\u0027\u0027 of the myth of Islamic tolerance\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perplexity", "{\"term\":\"perplexity\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027perplexité\u0027 or post-classical Latin \u0027perplexitas\u0027 ‘entanglement’, from \u0027perplexus\u0027.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state or quality of being perplexed; puzzled or confused\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Something that perplexes\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"1942\u0027\u0027, Rebecca West, \u0027Black Lamb and Grey Falcon\u0027 (Canongate 2006), page 149\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The Emperor, who was by then a focus of unresolvable \u0027\u0027perplexities\u0027\u0027, stood providing a strongly contrary appearance\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A measurement in information theory: see\", \"priority\":5}]}, \"synonyms\":{}}");

	add("perquisite", "{\"term\":\"perquisite\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\"], \"text\":\"From \u0027perquisitum\u0027 (something acquired for profit).\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Any monetary or other incidental benefit beyond salary\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The perquisites of this job include health insurance and a performance bonus\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A gratuity\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"After the wonderful service that evening he didn’t hesitate in laying a substantial perquisite on the table\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A privilege or possession held or claimed exclusively by a certain person, group or class\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"Private jets and motor yachts are a perquisite of the rich\", \"priority\":6}]}, \"synonyms\":{}}");

	add("persimmon", "{\"term\":\"persimmon\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"powhatan\", \"unami\"], \"text\":\"From Powhatan \u0027pichamins\u0027 \u0027pushemins\u0027 \u0027pasimenan\u0027 (dried fruit) from the same Proto-algonquian root {{m|alg-pro|*mini|*-imin|fruit, berry}} as Unami \u0027ximin\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A type of fruit, of orange colour, very sweet, quite astringent when immature\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The tree this fruit grows on, generally one of two species of ebony: (Asian) or (North American\", \"priority\":2}]}, \"synonyms\":{}}");

	add("persimoni", "{\"term\":\"persimoni\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Finnish\"], \"text\":\"From English \u0027en\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"persimmon\", \"priority\":1}]}, \"synonyms\":{}}");

	add("personage", "{\"term\":\"personage\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"old french (842-ca. 1400)\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027personnage\u0027 from Old french (842-ca. 1400) \u0027personage\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A famous or important person\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1872\u0027\u0027, w:Oliver Wendell Holmes, Oliver Wendell Holmes [Sr.], \u0027The Poet at the Breakfast-Table\u0027, [http://books.google.com/books?id\u003dghFB7jcBlQUC\u0026amp;pg\u003dPA230\u0026amp;dq\u003dpersonage page 230\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"I can only say they have been in pretty close conversation several times of late, and, if I dared to think it of so very calm and dignified a \u0027\u0027personage\u0027\u0027, I should say that her color was a little heightened after one or more of these interviews\", \"priority\":3}]}, \"synonyms\":{}}");

	add("personality", "{\"term\":\"personality\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle english (1100-1500)\", \"middle french (ca. 1400-1600)\"], \"text\":\"Coined between 1350 and 1400 from Middle english (1100-1500) {{m|enm|personalite}}, from Middle french (ca. 1400-1600) from {{m|la|persōnālitās}}.\u0026lt;ref\u0026gt;{{R:Dictionary.com}}\u0026lt;/ref\u0026gt;\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A set of qualities that make a person (or thing) distinct from another\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Personality\u0027\u0027 is individuality existing in itself, but with a nature as a ground\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"chapter\u003d19|url\u003dhttp://openlibrary.org/works/OL2004261W|passage\u003dMeanwhile Nanny Broome was recovering from her initial panic and seemed anxious to make up for any kudos she might have lost, by exerting her \u0027\u0027personality\u0027\u0027 to the utmost. She took the policeman\u0027s helmet and placed it on a chair, and unfolded his tunic to shake it and fold it up again for him\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"An assumed role or manner of behavior\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A celebrity\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"Charisma, or qualities that make a person stand out from the crowd\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"1959\u0027\u0027, Lloyd Price, “Personality”\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"But over and over / I´ll be a fool for you / \u0027cause you got \u0027\u0027personality\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"Something said or written which refers to the person, conduct, etc., of some individual, especially something of a disparaging or offensive nature; personal remarks\", \"priority\":9},{\"upperType\":\"NOUN\", \"text\":\"Sharp \u0027\u0027personalities\u0027\u0027 were exchanged\", \"priority\":10},{\"upperType\":\"NOUN\", \"text\":\"1905\u0027\u0027, \u0027s:Telemachus, Telemachus, Friend\", \"priority\":11},{\"upperType\":\"NOUN\", \"text\":\"Perceiving that \u0027\u0027personalities\u0027\u0027 were not out of order, I asked him what species of beast had long ago twisted and mutilated his left ear\", \"priority\":12},{\"upperType\":\"NOUN\", \"text\":\"That quality of a law which concerns the condition, state, and capacity of persons\", \"priority\":13}]}, \"synonyms\":{}}");

	add("personalization", "{\"term\":\"personalization\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|personalize|ation|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The act of personalizing something\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The offering of user-specific customization\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The act of changing an option of a multi-user software product to change the product\u0027s behavior or style for one user\", \"priority\":3}]}, \"synonyms\":{}}");

	add("personalty", "{\"term\":\"personalty\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|personal|ty|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Any property that is movable; that is, not real estate\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The state of being a person; personality\", \"priority\":2}]}, \"synonyms\":{}}");

	add("personalzine", "{\"term\":\"personalzine\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{compound|personal|zine|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A fanzine produced by a single person, often covering that person\u0027s own interests and activities\", \"priority\":1}]}, \"synonyms\":{}}");

	add("personification", "{\"term\":\"personification\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|personify|ication|alt1\u003dPersonify|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A person, thing or name typifying a certain quality or idea; an embodiment or exemplification\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Adolf Hitler was the \u0027\u0027personification\u0027\u0027 of anti-Semitism\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A figure of speech,in which an inanimate object or an idea is given human qualities\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The writer used \u0027\u0027personification\u0027\u0027 to convey her ideas\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"An artistic representation of an abstract quality as a huma\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"The Grim Reaper is a \u0027\u0027personification\u0027\u0027 of death\", \"priority\":6}]}, \"synonyms\":{}}");

	add("personnel", "{\"term\":\"personnel\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\"], \"text\":\"From French \u0027personnel\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"employees; office staff\", \"priority\":1}]}, \"synonyms\":{}}");

	add("persons", "{\"term\":\"persons\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"used to refer to them individually, rather than as a group. Contrast\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2007\u0027\u0027 May 14: The Bureau of Labor Statistics of the U.S. Department of Labor, \u0027Current Population Survey Frequently Asked Questions — [http://www.bls.gov/cps/cps_faq.ht\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Ques5 Question 5]: “Who is counted as unemployed?”\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"Persons\u0027\u0027 are classified as unemployed if they do not have a job, have actively looked for work in the prior 4 weeks, and are currently available for work\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"passage\u003dSimilar studies of rats have employed four different intracranial resorbable, slow sustained release systems\u0026amp;mdash;\u0026amp;nbsp;[\u0026amp;hellip;]. Such a slow-release device containing angiogenic factors could be placed on the pia mater covering the cerebral cortex and tested in \u0027\u0027persons\u0027\u0027 with senile dementia in long term studies\", \"priority\":5}]}, \"synonyms\":{}}");

	add("perspective", "{\"term\":\"perspective\", \"etymology\":{\"influencers\":[{},{}], \"languages\":[\"English\", \"middle english (1100-1500)\", \"middle french (ca. 1400-1600)\", \"Italian\"], \"text\":\"Middle english (1100-1500) attested since 1381, from Old - or Middle french (ca. 1400-1600) from the first word of the \u0027perspectiva\u0027 the feminine of \u0027perspectivus\u0027 (of sight, optical) from \u0027perspectus\u0027 the past participle of \u0027perspicere\u0027 (to inspect, look through) itself from \u0027per-\u0027 (through) + \u0027specere\u0027 (to look at) the noun sense was influenced or mediated by Italian \u0027prospettiva\u0027 from \u0027prospetto\u0027 (prospect)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A view, vista or outlook\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The appearance of depth in objects, especially as perceived using binocular vision\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The technique of representing three-dimensional objects on a two-dimensional surface\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The choice of a single angle or point of view from which to sense, categorize, measure or codify experience\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"The ability to consider things in such relative perspectiv\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"A perspective glass\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"Bishop Joseph Hal\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"our predecessors; who could never have believed, that there were such lunets about some of the planets, as our late \u0027\u0027perspectives\u0027\u0027 have descried\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"A sound recording technique to adjust and integrate sound sources seemingly naturally\", \"priority\":9}]}, \"synonyms\":{}}");

	add("perspicacity", "{\"term\":\"perspicacity\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027perspicacité\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Acute discernment or understanding; insight\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1904\u0027\u0027, \u0027The Sea-Wolf\u0027, ch. 8\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"quot;I understand,\u0026quot; I said. \u0026quot;The fact is that you have the money.\u0026quot; His face brightened. He seemed pleased at my \u0027\u0027perspicacity\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The human faculty or power to mentally grasp or understand clearly\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"1856\u0027\u0027, \u0026quot;Selections from the Letters of Robert Southey,\u0026quot; \u0027The Quarterly Review\u0027, vol. 98, p. 458\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"His very veneration for his father-in-law, combined as it is with a total want of the most ordinary \u0027\u0027perspicacity\u0027\u0027, is an additional disqualification\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"1888\u0027\u0027, \u0026quot;Review of \u0027La suggestion mentale\u0027 by H. Bourru and P. Burot,\u0026quot; \u0027The American Journal of Psychology\u0027, vol. 1 no. 3, p. 503\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"As the former consists in the transmission of psychic states inappreciable to the normal \u0027\u0027perspicacity\u0027\u0027 or senses, the transfer cannot pass through the medium of intelligence\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"Keen eyesight\", \"priority\":9},{\"upperType\":\"NOUN\", \"text\":\"1833\u0027\u0027, John Harrison Curtis, \u0027A Treatise on the Physiology and Diseases of the Eye\u0027, London, Longman, p. 138\", \"priority\":10},{\"upperType\":\"NOUN\", \"text\":\"Attentive consideration of the phenomena of vision has led to the invention of artificial aids by which the sight may be wonderfully strengthened and preserved, and man endowed at once with the \u0027\u0027perspicacity\u0027\u0027 of the eagle or the minute scrutiny of the insect\", \"priority\":11}]}, \"synonyms\":{}}");

	add("perspicuity", "{\"term\":\"perspicuity\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\", \"Latin\"], \"text\":\"From the Classical Latin \u0027perspicuitās\u0027 (transparency”, “lucidity”, “self-evidency”, {{sense) “penetration”, “insight|lang\u003dla}}, from \u0027perspicuus\u0027 (clear”, “evident) compare \u0027perspicacity\u0027 and the French \u0027perspicuité\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Clarity, lucidity, especially in expression; the state or characteristic of being perspicuous\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1905\u0027\u0027, \u0027The Irrational Knot\u0027, ch. 18\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Whether through the first officer\u0027s want of \u0027\u0027perspicuity\u0027\u0027 or my own stupidity, I was not a bit the wiser for the explanation\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"Perspicacity; insight\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"chapter\u003d4|passage\u003d“Well,” I answered, at first with uncertainty, then with inspiration, “he would do splendidly to lead your cotillon, if you think of having one.” \u0026amp;para; “So you do not dance, Mr. Crocker?” \u0026amp;para; I was somewhat set back by her \u0027\u0027perspicuity\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"1965\u0027\u0027, \u0026quot;[http://www.time.com/time/magazine/article/0,9171,833549,00.html The Stupid Spy],\u0026quot; \u0027Time\u0027, 19 Mar\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"Thompson, a high school dropout, said with rare \u0027\u0027perspicuity\u0027\u0027 that he doubted the FBI would hire him\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"Transparency; translucence\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"1900\u0027\u0027, \u0027The Touchstone\u0027, ch. 11\", \"priority\":9},{\"upperType\":\"NOUN\", \"text\":\"It must have been on some such day of harsh sunlight, the incisive February brightness that gives \u0027\u0027perspicuity\u0027\u0027 without warmth\", \"priority\":10}]}, \"synonyms\":{}}");

	add("perspiration", "{\"term\":\"perspiration\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\", \"Latin\"], \"text\":\"From French \u0027perspiration\u0027 from \u0027perspirer\u0027 (lang\u003dfr) from Latin \u0027perspirare\u0027 (lang\u003dla) from \u0027per\u0027 (lang\u003dla) + \u0027spirare\u0027 (lang\u003dla)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"the action or process of perspirin\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"a saline fluid secreted by the sweat glands\", \"priority\":2}]}, \"synonyms\":{}}");

	add("persuasiveness", "{\"term\":\"persuasiveness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"* {{suffix|persuasive|ness|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The capability of a person or argument to convince or persuade someone to accept a desired way of thinking\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1919\u0027\u0027, w:P. G. P. G. Wodehouse, \u0027w:My Man My Man Jeeves\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"By using the utmost tact and \u0027\u0027persuasiveness\u0027\u0027, he was inducing his uncle to cough up very grudgingly a small quarterly allowance\", \"priority\":3}]}, \"synonyms\":{}}");

	add("persymmetry", "{\"term\":\"persymmetry\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|per|symmetry|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The property of being persymmetric\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pertainym", "{\"term\":\"pertainym\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|pertain|nym|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"a word, usually an adjective, which can be defined as \u0026quot;of or pertaining to\u0026quot; another word\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pertness", "{\"term\":\"pertness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{suffix|pert|ness|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The quality of being pert; cheekiness, impudence\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1983\u0027\u0027, \u0027Sebastian\u0027, Faber \u0026amp; Faber 2004 , p. 1021\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"He was completely bald and had a heavy, Chinese-looking countenance which contained, deeply embedded, two eyes of penetration and \u0027\u0027pertness\u0027\u0027, always twinkling on the edge of laughter\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The quality of being pert; firmness (usually of breasts\", \"priority\":4}]}, \"synonyms\":{}}");

	add("perturbation", "{\"term\":\"perturbation\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"old french (842-ca. 1400)\", \"Latin\"], \"text\":\"From Middle french (ca. 1400-1600) from Old french (842-ca. 1400) \u0027perturbacion\u0027 from Latin \u0027perturbatio\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Agitation; the state of being perturbe\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A small change in a physical system, or more broadly any definable system (such as a biological or economic system\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Variation in an orbit due to the influence of external bodies\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perturber", "{\"term\":\"perturber\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|perturb|er|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A source of perturbatio\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"This moon is a significant \u0027\u0027perturber\", \"priority\":2}]}, \"synonyms\":{}}");

	add("peruke", "{\"term\":\"peruke\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\"], \"text\":\"From French \u0027perruque\u0027.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A wig, especially one with long hair on the sides and back, worn mainly by men in the 17th and 18th centuries\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1819\u0027\u0027, Lord Byron, \u0027Don Juan\u0027, I.213\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"But now at thirty years my hair is grey— / (I wonder what it will be like at forty? / I thought of a \u0027\u0027peruke\u0027\u0027 the other day—\", \"priority\":3}]}, \"synonyms\":{}}");

