	add("perfume", "{\"term\":\"perfume\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"Italian\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027parfum\u0027 from the obsolete Italian \u0027parfumare\u0027 \u0026quot;to smoke through\u0026quot;\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A pleasant smell; the scent, odor, or odoriferous particles emitted from a sweet-smelling substance; a pleasant odor; fragrance; aroma\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Breezes blowing from beds of iris quickened her breath with their \u0027\u0027perfume\u0027\u0027; she saw the tufted lilacs sway in the wind, and the streamers of mauve-tinted wistaria swinging, all a-glisten with golden bees; she saw a crimson cardinal winging through the foliage, and amorous tanagers flashing like scarlet flames athwart the pines\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A substance created to provide a pleasant smell or one which emits an agreeable odor\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"title\u003d[http://www.theguardian.com/business/2014/mar/04/chanel-perfume-legislation-guerlain-loreal Perfume manufacturers must cope with the scarcity of precious passage\u003dThe \u0027\u0027perfume\u0027\u0027 industry is facing a major problem: maintaining constant levels of quality is crucial, but it is increasingly difficult to obtain a regular supply of all the necessary natural ingredients\", \"priority\":4}]}, \"synonyms\":{}}");

	add("perfumer", "{\"term\":\"perfumer\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{suffix|perfume|er|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A person who makes or sells perfume\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1842\u0027\u0027, ‘The Mystery of Marie Rogêt’\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Affairs went on thus until the latter had attained her twenty-second year, when her great beauty attracted the notice of a \u0027\u0027perfumer\u0027\u0027, who occupied one of the shops in the basement of the Palais Royal, and whose custom lay chiefly among the desperate adventurers infesting that neighborhood\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perfunctoriness", "{\"term\":\"perfunctoriness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{compound|perfunctory|ness|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state or characteristic of being perfunctory\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1894\u0027\u0027, w:Thomas Thomas Hardy, \u0027Life\u0027s Little Ironies\u0027, ch. 6\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"When at dusk she reached the Melchester station her husband was there to meet her, but in his \u0027\u0027perfunctoriness\u0027\u0027 and her preoccupation they did not see each\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"1901\u0027\u0027, w:Edith Edith Wharton, \u0026quot;The Recovery\u0026quot; in \u0027Crucial Instances\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"Claudia accomplished some shopping in the spirit of \u0027\u0027perfunctoriness\u0027\u0027 that robs even new bonnets of their bloom\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"2006\u0027\u0027 Nov. 30, David Cohen, \u0026quot;[http://www.nysun.com/arts/bit-nasty-to-women-but-respectful-to-dishware/44356/ A Bit Nasty to Women, But Respectful to Dishware],\u0026quot; \u0027New York Sun\u0027 \u0026lt;small\u0026gt;(retrieved 28 June 2011)\u0026lt;/small\u0026gt\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"His hard-core images are delivered with a ho-hum \u0027\u0027perfunctoriness\u0027\u0027 that often enervates his surfaces. . . . [H]is orgies are inert\", \"priority\":7}]}, \"synonyms\":{}}");

	add("perfusion", "{\"term\":\"perfusion\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"Latin\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027perfusion\u0027 from Latin \u0027perfusio\u0027 (the act of pouring over)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"File:Lindbergh perfusion pump in Putnam Gallery, thumb|upright|A perfusion pump, c. 1935, an early device for simulating natural \u0027\u0027perfusion\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The act of perfusin\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The introduction of a drug or nutrients through the bloodstream in order to reach an internal organ or tissues\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perfusionist", "{\"term\":\"perfusionist\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|perfusion|ist|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A trained clinician who operates the heart-lung machine during cardiac and other surgeries\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pergamum", "{\"term\":\"pergamum\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Latin \u0027pergamum\u0027 from Ancient greek (to 1453) \u0027πέργαμον\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An ancient Greek city, in western Anatolia, near modern Bergama\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pergola", "{\"term\":\"pergola\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Italian\", \"Latin\"], \"text\":\"From Italian \u0027pergola\u0027 from Latin \u0027pergula\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A framework in the form of a passageway of columns that supports a trelliswork roof; used to support and train climbing plant\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1919,\u0027\u0027 w:Ronald Ronald Firbank, Valmouth\u0027, Duckworth, hardback edition, page 61\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"By the little garden \u0027\u0027pergola\u0027\u0027 open to the winds some fluttered peacocks were blotted nervelessly amid the dripping trees, their heads sunk back beneath their wings: while in the \u0027\u0027pergola\u0027\u0027 itself, like a fallen storm-cloud, lolled a negress, her levelled, polecat eyes semi-veiled by the nebulous alchemy of the rainbow\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2000\u0027\u0027, Gordon Bock, \u0026quot;[http://books.google.com/books?id\u003dZhO-43iXnLcC\u0026amp;pg\u003dPT56 Pergolas in perspective]\u0026quot;, \u0027Old-House Journal\u0027, July/August 2000\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"While both \u0027\u0027pergolas\u0027\u0027 and arbors are most dramatic cloaked in climbing, flowing plants, only a \u0027\u0027pergola\u0027\u0027 will stand naked as a piece of architecture\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"2009\u0027\u0027, Jerri Farris, \u0027Creating Garden Accents: Step-by-Step Instructions for 22 Projects\u0027, Creative Publishing International (2002), ISBN 1589230418, [http://books.google.com/books?id\u003du4vF5j9ZXYoC\u0026amp;pg\u003dPA98 page 98\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"When I first saw the movie, \u0027Chocolat\u0027, my favorite element wasn\u0027t the plot or the character development or even the cinematography. It was the cloth-draped \u0027\u0027pergola\u0027\u0027 under which a very special birthday dinner was served\", \"priority\":7}]}, \"synonyms\":{}}");

	add("pergolide", "{\"term\":\"pergolide\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A dopamine receptor agonist used for the treatment of Parkinson\u0027s disease\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perhydrosqualene", "{\"term\":\"perhydrosqualene\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The fully saturated triterpene derived from squalene\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peril", "{\"term\":\"peril\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"old french (842-ca. 1400)\", \"Latin\"], \"text\":\"From Old french (842-ca. 1400) \u0027peril\u0027 from Latin \u0027perīculum\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A situation of serious and immediate danger\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Something that causes, contains, or presents danger\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The perils of the jungle\u0027 (animals and insects, weather, etc\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"An event which causes a loss, or the risk of a specific such event\", \"priority\":4}]}, \"synonyms\":{}}");

	add("periapex", "{\"term\":\"periapex\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|apex|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The area around an ape\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periapsis", "{\"term\":\"periapsis\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|apsis|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The point of a body\u0027s elliptical orbit about the system\u0027s centre of mass where the distance between the body and the centre of mass is at its minimum\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periastron", "{\"term\":\"periastron\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) {{l|grc|περί}} \u0026quot;around\u0026quot; + {{l|grc|ἄστρον}} \u0026quot;star\u0026quot;.{{prefix|peri|astron|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"That point of the orbit of a celestial body which is closest to the star around which the body is orbiting\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periblast", "{\"term\":\"periblast\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{confix|peri|blast|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The protoplasmic matter which surrounds the entoblast, or cell nucleus, and undergoes segmentation\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pericarp", "{\"term\":\"pericarp\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\", \"ancient greek (to 1453)\"], \"text\":\"From French \u0027péricarpe\u0027 from \u0027pericarpium\u0027 from Ancient greek (to 1453) \u0027περικάρπιον\u0027 (pod, husk, shell) from \u0027περί\u0027 (around) + \u0027καρπός\u0027 (fruit) Alternatively, {{confix|peri|carp|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The outermost layer, or skin, of a ripe fruit or ovary\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The outer layer of any thing\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"1974\u0027\u0027, \u0027Tatlin\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"He learned, afternoon after afternoon, how to edge her into delicious frenzies, how to tremble the clitoris, \u0027\u0027pericarp\u0027\u0027, and tip\", \"priority\":4}]}, \"synonyms\":{}}");

	add("pericenter", "{\"term\":\"pericenter\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|center|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The periapsis, the point of closest approach of an astronomical object in an elliptical orbit to its center of attraction\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pericentrin", "{\"term\":\"pericentrin\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|centrin|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A protein that binds to calmodulin and is expressed in the centrosome, an integral component of the pericentriolar material\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perichoresis", "{\"term\":\"perichoresis\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περιχώρησις\u0027 (going round, rotation) from \u0027περιχωρέω\u0027 () from \u0027περί\u0027 (around) + \u0027χωρεω\u0027 (go forward, travel) because the relationship between the three members of the Trinity was described by early Christians as an eternal interpenetrating circulation.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The doctrine of the Trinity\", \"priority\":1}]}, \"synonyms\":{}}");

	add("pericles", "{\"term\":\"pericles\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Latin \u0027periclēs\u0027 from Ancient greek (to 1453) \u0027περικλῆς\u0027 (far-famed) from \u0027περί\u0027 (around) + \u0027κλέος\u0027 (glory)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A Greek politician that lived during the ancient and the classical times\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"of mostly historical use\", \"priority\":2}]}, \"synonyms\":{}}");

	add("pericope", "{\"term\":\"pericope\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\"], \"text\":\"From {{m|la|pericope}}, from Greek \u0027περικοπή\u0027 ‘section’, from \u0027peri-\u0027 + \u0027κοπή\u0027 ‘cutting’, from \u0027κόπτειν\u0027 ‘to cut’.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A section of text forming a coherent thought, suitable for use in a speech\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A passage of Scripture to be read in public worship or a book containing such passages\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The reader looked up the Sunday morning \u0027\u0027pericope\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The oldest known system of \u0027\u0027pericopes\u0027\u0027 in the Western Church is ascribed to Jerome\", \"priority\":4}]}, \"synonyms\":{}}");

	add("periderm", "{\"term\":\"periderm\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{confix|peri|derm|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The outer layer of plant tissue; the outer bark\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The perisarc; the hard outer layer of hydroids and other marine animals\", \"priority\":2}]}, \"synonyms\":{}}");

	add("peridot", "{\"term\":\"peridot\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\", \"middle french (ca. 1400-1600)\", \"anglo-norman\"], \"text\":\"From Anglo-norman \u0027peridou\u0027 \u0027\u0027 Middle french (ca. 1400-1600) \u0027perido\u0027 \u0027\u0027 of uncertain origin; later reborrowed from modern French \u0027péridot\u0027 Image:Olivine (peridot).jpg|thumb|Peridot\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A transparent olive-green form of olivine, used as a gem\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2004\u0027\u0027, \u0027The Earth\u0027, Folio Society 2011, p. 51\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Gem-quality olivine is known as \u0027\u0027peridot\u0027\u0027, which has a subtle green light all its own\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"passage\u003dAlthough there are dozens of different types of gems, among the best known and most important are\u0026amp;nbsp;. (Common gem materials not addressed in this article include amber, amethyst, chalcedony, garnet, lazurite, malachite, opals, \u0027\u0027peridot\u0027\u0027, rhodonite, spinel, tourmaline, turquoise and zircon\", \"priority\":4}]}, \"synonyms\":{}}");

	add("peridotite", "{\"term\":\"peridotite\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{suffix|peridot|ite|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A rock consisting of small crystals of olivine, pyroxene and hornblende; the major constituent of the Earth\u0027s mantle\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2004\u0027\u0027, \u0027The Earth\u0027, Folio Society 2011, p. 60\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Varieties of \u0027\u0027peridotite\u0027\u0027 are distinguished by their mineral composition; for example, dunite is a \u0027\u0027peridotite\u0027\u0027 which is virtually all olivine, and a dense, dark green in colour\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perilla", "{\"term\":\"perilla\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\"], \"text\":\"From\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The plant shiso\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perimenopause", "{\"term\":\"perimenopause\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περί\u0027 (about, around, near) + \u0027μήν\u0027 (month) + \u0027παῦσις\u0027 (pause)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The physiological stage menstruating women go through when approaching menopause, when, due to hormonal changes, they progress from their usual pattern of menstruation through a phase of atypical menstruation, and finally cease menstruating. Perimenopause ends when a woman has not menstruated for a year\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2009\u0027\u0027, Robert Preidt, \u0027Study to Assess Hormone Therapy Before Menopause\u0027, \u0027ABC News\u0027, [http://abcnews.go.com/Health/Healthday/story?id\u003d4507681\u0026amp;page\u003d1] (Mar. 23 2009\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Researchers at eight locations across the United States plan to examine the safety and effectiveness of estrogen therapy during \u0027\u0027perimenopause\u0027\u0027, the few years just prior to menopause\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perimeter", "{\"term\":\"perimeter\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περίμετρος\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The sum of the distance of all the lengths of the sides of an object\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The length of such a boundary\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The outer limits of an area. See synonyms at circumference\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"A fortified strip or boundary usually protecting a military position\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"An instrument for determining the extent and shape of the field of vision\", \"priority\":5}]}, \"synonyms\":{}}");

	add("perimycin", "{\"term\":\"perimycin\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A polyene antibiotic with antifungal properties, produced by the bacterium \u0027Streptomyces coelicolor\u0027 var. \u0027aminophilus\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perinatologist", "{\"term\":\"perinatologist\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|perinatology|ist|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An obstetrician specializing in the care of the mother and fetus at higher than normal risk for complications\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perinatology", "{\"term\":\"perinatology\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Ancient greek (to 1453) \u0027περί\u0027 (around or about) + Latin \u0027natus\u0027 + \u0027-ology\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A branch of obstetrics focusing on the medical and surgical management of high-risk pregnancies\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perinephritis", "{\"term\":\"perinephritis\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|nephritis|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"inflammation of the cellular tissue around the kidney\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perineum", "{\"term\":\"perineum\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From post‐Classical Latin \u0027perineum\u0027 () (Classical Latin \u0027perinaeon\u0027 , from Ancient greek (to 1453) \u0027περίναιον\u0027 or \u0027περίνεος\u0027 from \u0027περί\u0027 (around) with \u0027ἰνέω\u0027 (i empty, evacuate)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The region between the anus and the vagina in women and between the scrotum and anus in men\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1827\u0027\u0027, \u0027Guy\u0027s Hospital: Case of Fistula in Perineo, Successfully Treated\u0027, \u0027The Lancet\u0027, Volume 12, [http://books.google.com.au/books?id\u003d5RpAAAAAcAAJ\u0026amp;pg\u003dPA61\u0026amp;dq\u003d%22perineum%22%7C%22perinea%22\u0026amp;hl\u003den\u0026amp;sa\u003dX\u0026amp;ei\u003dOkvdUdnRCLCfiAetw4HwDQ\u0026amp;redir_esc\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"v\u003donepage\u0026amp;q\u003d%22perineum%22%7C%22perinea%22\u0026amp;f\u003dfalse page 61\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"He was attacked with complete retention of urine, after a debauch, and six weeks afterwards an abscess formed in the \u0027\u0027perineum\u0027\u0027, which eventually burst, leaving a fistulous opening, through which the urine continued to be discharged to the period of his admission into the Hospital\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"2005\u0027\u0027, Brian J. Moran, \u002727: Using the perineal pressure applicator device\u0027, Adam P. Dicker, Gregory S. Merrick, Frank M. Waterman, Richard K. Valicenti, Leonar G. Gomella (editors), \u0027Basic and Advanced Techniques in Prostate Brachytherapy\u0027, [http://books.google.com.au/books?id\u003dzwrNHNK4UbUC\u0026amp;pg\u003dPA251\u0026amp;dq\u003d%22perineum%22%7C%22perinea%22\u0026amp;hl\u003den\u0026amp;sa\u003dX\u0026amp;ei\u003dOkvdUdnRCLCfiAetw4HwDQ\u0026amp;redir_esc\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"v\u003donepage\u0026amp;q\u003d%22perineum%22%7C%22perinea%22\u0026amp;f\u003dfalse page 251\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"The prostate is surrounded by venous plexi and the \u0027\u0027perineum\u0027\u0027 has a rich blood supply\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"2011\u0027\u0027, Ian Lyons, \u0027Biomedical Science: Lecture Notes\u0027, [http://books.google.com.au/books?id\u003dEcQHNXuqx-cC\u0026amp;pg\u003dPA371\u0026amp;dq\u003d%22perineum%22%7C%22perinea%22\u0026amp;hl\u003den\u0026amp;sa\u003dX\u0026amp;ei\u003dOkvdUdnRCLCfiAetw4HwDQ\u0026amp;redir_esc\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"v\u003donepage\u0026amp;q\u003d%22perineum%22%7C%22perinea%22\u0026amp;f\u003dfalse page 371\", \"priority\":9},{\"upperType\":\"NOUN\", \"text\":\"The \u0027\u0027perineum\u0027\u0027 is diamond shaped and stretches from the pubic symphysis to the coccyx\", \"priority\":10}]}, \"synonyms\":{}}");

	add("periodentosis", "{\"term\":\"periodentosis\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{rfe|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"inflammation of soft tissues around one\u0027s teeth such as the gums\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periodical", "{\"term\":\"periodical\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|periodic|al|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A publication issued regularly, but less frequently than daily\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A publication that appears at fixed intervals\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A publication that often contains the most current information in the field, on every conceivable topic, often in greater detail than other publication formats\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The primary means for communication of original scholarship or creative work at the cutting edge of research in almost all fields\", \"priority\":4}]}, \"synonyms\":{}}");

	add("periodontics", "{\"term\":\"periodontics\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The study of supporting structures of teeth—gums, alveolar bone, cementum, and the periodontal ligament—and diseases and conditions that affect them\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periodontitis", "{\"term\":\"periodontitis\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περί\u0027 (around) + \u0027ὀδούς\u0027 (a tooth) + \u0027\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Any of a number of inflammatory diseases affecting the periodontium—the tissues that surround and support the teeth\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periodontium", "{\"term\":\"periodontium\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The specialized tissues that both surround and support the teeth, maintaining them in the maxillary and mandibular bones; the tissues including alveolar bone, cementum, gums and periodontal ligament\", \"priority\":1}]}, \"synonyms\":{}}");

	add("periodontology", "{\"term\":\"periodontology\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Periodontics\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peripatetic", "{\"term\":\"peripatetic\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Latin \u0027peripatēticus\u0027 from Ancient greek (to 1453) \u0027περίπατος\u0027 (lang\u003dgrc) from \u0027περιπατέω\u0027 (i walk around) from \u0027περί|lang\u003dgrc\u0027 () + \u0027πατέω\u0027 (i walk) Aristotle’s school was sometimes called the {{l|grc|περιπατητικοί}} \u0026quot;those who are prone to walking\u0026quot; or \u0027\u0027 (οἱ ἐκ τοῦ περιπάτου) in reference either to his supposed habit of teaching while traversing the \u0027περίπατοι\u0027 (walkways) of the lyceum|Lyceum or simply to the walkways themselves with which the school became associated.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A disciple of Aristotle; an Aristotelian\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1661\u0027\u0027, w:Robert Robert Boyle, \u0027s:Sceptical The Sceptical Chymist\u0027, p. A5(w:recto and r\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"And though it be True indeed, that some Aristotelians have occasionally written against the Chymical Doctrine he Oppugnes, yet since they have done it according to their Principles, And since our Carneades must as well oppose their Hypothesis as that of the Spagyrist, he was fain to fight his Adverfaries with their own Weapons, Those of the \u0027\u0027Peripatetick\u0027\u0027 being Improper, if not hurtfull for a Person of his Tenents ; besides that those Aristotelians, (at Least, those he met with,) that have written against the Chymists, seem to have had so little Experimental Knowledge in Chymical Matters, that by their frequent Mistakes and unskilfull way of Oppugning, they have too often expos\u0027d Themselves to the Derision of their Adversaries, for writing so Confidently against what they appear so little to understand\", \"priority\":3}]}, \"synonyms\":{}}");

	add("peripatry", "{\"term\":\"peripatry\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state or condition of being peripatric\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peripeteia", "{\"term\":\"peripeteia\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From \u0027peripetia\u0027 and its source Ancient greek (to 1453) \u0027περιπέτεια\u0027 ultimately from \u0027περί\u0027 (round, around, about) + the stem of \u0027πίπτω\u0027 (to fall)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A sudden reversal of fortune as a plot point in Classical tragedy; hence, any sudden change in circumstances; a crisis\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1965\u0027\u0027, \u0027The Magus\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Once more I was a man in a myth, incapable of understanding it, but somehow aware that understanding it meant it must continue, however sinister its \u0027\u0027peripeteia\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"1977\u0027\u0027, \u0027A Savage War of Peace\u0027, New York Review books 2006, p. 167\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"They were to bestride the Algerian scene like demigods until the tragic \u0027\u0027peripeteia\u0027\u0027 of 1961\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"A turning point in psychosocial development\", \"priority\":6}]}, \"synonyms\":{}}");

	add("periphrases", "{\"term\":\"periphrases\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"circumlocutions\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"De Quince\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"To describe by enigmatic \u0027\u0027periphrases\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"use of syntactic rather than morphological constructions\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"Language learners sometimes use \u0027\u0027periphrases\u0027\u0027 like \u0026quot;did go\u0026quot; where a native speaker would use \u0026quot;went\u0026quot\", \"priority\":5}]}, \"synonyms\":{}}");

	add("periphrasis", "{\"term\":\"periphrasis\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) {{m|grc|περίφρασις}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The use of a longer expression instead of a shorter one with a similar meaning, for example \u0026quot;I am going to\u0026quot; instead of \u0026quot;I will\u0026quot\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Expressing a grammatical meaning (such as a tense) using a syntactic construction rather than morphological marking\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The substitution of a descriptive word or phrase for a proper name (a species of circumlocution\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The use of a proper name as a shorthand to stand for qualities associated with it\", \"priority\":4}]}, \"synonyms\":{}}");

	add("periplus", "{\"term\":\"periplus\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"Latin \u0027periplus\u0027 from Ancient greek (to 1453) \u0027περίπλους\u0027 (circumnavigation) from \u0027περί\u0027 (around) + \u0027πλέω\u0027 (i sail)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A circumnavigation; a sea voyage around a coastline\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A record of such a voyage\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"1974\u0027\u0027, Guy Davenport, \u0027Tatlin\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"the map of the world Ptolemy followed in his geography, finding the longitude that passed through the Gates of Herakles and the island of Rhodes, and placing north and south of it the world as recorded on the \u0027\u0027periploi\u0027\u0027 of the Phoenician merchants\", \"priority\":4}]}, \"synonyms\":{}}");

	add("peripuberty", "{\"term\":\"peripuberty\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|puberty|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The time around puberty\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perishable", "{\"term\":\"perishable\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle english (1100-1500)\", \"middle french (ca. 1400-1600)\"], \"text\":\"Middle english (1100-1500) \u0027perysabyl\u0027 from Middle french (ca. 1400-1600) \u0027périssable\u0027 See {{suffix|perish|able|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"That which perishes or is short-lived\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"food that does not keep for long\", \"priority\":2}]}, \"synonyms\":{}}");

	add("perishableness", "{\"term\":\"perishableness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|perishable|ness|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state of being perishable; perishability\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perispomenon", "{\"term\":\"perispomenon\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From the Ancient greek (to 1453) {{m|grc|περισπώμενον}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A with a on the last\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peristalsis", "{\"term\":\"peristalsis\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περισταλτικός\u0027 () \u0027δύναμις\u0027 (peristaltic action of the bowels, by which their contents are propelled) from \u0027περιστέλλω\u0027 (wrap around) from \u0027περί\u0027 (around) + \u0027στέλλω\u0027 (i place)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The rhythmic, wave-like contraction of the digestive tract that forces food through it\", \"priority\":1}]}, \"synonyms\":{}}");

	add("peristyle", "{\"term\":\"peristyle\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From French \u0027péristyle\u0027 from Classical Latin \u0027peristȳlum\u0027 \u0027peristȳlium\u0027 from Ancient greek (to 1453) \u0027περιστῡ́λιον\u0027 \u0027περίστυλον\u0027 noun use of the neuter form of \u0027περίστυλος\u0027 (surrounded by columns) from \u0027περί\u0027 + \u0027στῦλος\u0027 (pillar)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"colonnade surrounding a courtyard, temple, etc\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1942\u0027\u0027, Rebecca West, \u0027Black Lamb and Grey Falcon\u0027 (Canongate 2006), page 143\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"One cannot, for example, see the Temple of Æsculapius as one stands in the fine open courtyard as it was intended one should do; the interstices on that side of the \u0027\u0027peristyle\u0027\u0027 have been blocked by Venetian Gothic buildings\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"A courtyard; the space surrounded by a colonnade\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A porch surrounded by columns\", \"priority\":5}]}, \"synonyms\":{}}");

	add("peritenon", "{\"term\":\"peritenon\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|peri|tenon|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The connective tissue sheath surrounding a tendon\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perizoma", "{\"term\":\"perizoma\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Latin\"], \"text\":\"From Latin\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A loincloth worn by men and women in ancient Greece\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1974\u0027\u0027, Guy Davenport, \u0027Tatlin\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The traveller was in Erewhonian clothes to keep him inconspicuous, a conical felt hat with owl feather, yellow tabard, tasselled \u0027\u0027perizoma\u0027\u0027, belled sandals, and umbrella\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perjurer", "{\"term\":\"perjurer\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"someone who has committed perjury by lying under oat\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1607\u0027\u0027: Henry Ainsworth, \u0027The confession of faith of certayn English people living in exile, in the Low countreyes\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Here are none exempted or excluded, be they never so prophane or wretched, no Atheist, adulterer, thief, or murderer, no lyer, \u0027\u0027perjurer\u0027\u0027, Witch or conjurer, \u0026amp;c· al are one fellowship, one body, one Church\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"1841\u0027\u0027: Charles Dickens, \u0027The Old Curiosity Shop\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"Come in, you false witness, you \u0027\u0027perjurer\u0027\u0027, you suborner of evidence, come in\", \"priority\":5}]}, \"synonyms\":{}}");

	add("perjury", "{\"term\":\"perjury\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"Latin\", \"anglo-norman\"], \"text\":\"From Anglo-norman \u0027perjurie\u0027 from Latin \u0027perjurare\u0027 (swear falsely) from Latin {{prefix|per|jurare|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The deliberate giving of false or misleading testimony under oath\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"We declare under penalty of \u0027\u0027perjury\u0027\u0027 that the foregoing is true and correct and that this declaration was executed on March 22, 2005, at Seattle, Washington\", \"priority\":2}]}, \"synonyms\":{}}");

	add("perkinism", "{\"term\":\"perkinism\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|Perkins|ism|lang\u003den}}, after inventor {{w|Elisha Perkins}} (1741–1799).\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A pseudoscientific treatment in which two pointed metal rods (\u0026quot;tractors\u0026quot;) were waved over the body, supposed to cure inflammation, rheumatism, etc. by drawing off an electrical fluid\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perkins", "{\"term\":\"perkins\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"from Perkin, a medieval diminutive of Peter\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perkinsosis", "{\"term\":\"perkinsosis\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|Perkinsus|osis|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A disease of oysters, caused by the protist \u0027Perkinsus marinus\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perlection", "{\"term\":\"perlection\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"Latin\"], \"text\":\"From Latin \u0027perlēctiō\u0027 (the act of reading through) from \u0027perlegō\u0027 (read through) from \u0027per\u0027 (through) + \u0027legō\u0027 (read)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"the reading through of something\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1923\u0027\u0027, Edward Powys Mathers, translator, \u0027The Thousand Nights and One Night\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"When the \u0027\u0027perlection\u0027\u0027 of the Koran had been accomplished, the cloth was laid and all manner of meats and necessary things were brought\", \"priority\":3}]}, \"synonyms\":{}}");

	add("perlite", "{\"term\":\"perlite\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\"], \"text\":\"From French \u0027perle\u0027 (lang\u003dfr) + \u0027-ite\u0027 ()\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An amorphous volcanic glass formed by the hydration of obsidian\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The lightweight insulating material and aggregate resulting from expanding perlite glass by heat\", \"priority\":2}]}, \"synonyms\":{}}");

	add("perlocution", "{\"term\":\"perlocution\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From \u0027per-\u0027 + locution (from Latin \u0027locutio\u0027, from \u0027loqui\u0027 to speak)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The effect the terms used by a speaker can have on another speaker and their emotions and responses\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perlustration", "{\"term\":\"perlustration\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From \u0027perlustrate\u0027.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"the action or fact of perlustratin\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"thorough examination of a document or of correspondenc\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"1962\u0027\u0027: Yes, after a thorough \u0027\u0027perlustration\u0027\u0027 of the loot that Andron and Niagarushka had obtained from the Queen\u0027s rosewood writing desk (mostly bills, and treasured snapshots, and those silly medals) a letter from the King did turn up — Vladimir Nabokov, \u0027Pale Fire\", \"priority\":3}]}, \"synonyms\":{}}");

	add("permaban", "{\"term\":\"permaban\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|ban|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A permanent ban on a user\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Anyone caught cheating on our game server will receive a \u0027\u0027permaban\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permaboner", "{\"term\":\"permaboner\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|boner|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A penis that is persistently erect; a prolonged erection\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2008\u0027\u0027, Kiki Faran, \u0027Mr. Right Hand: An MF Spanking Novel\u0027, Lulu Press (2008), ISBN 9781435709027, [http://books.google.com/books?id\u003d0k9QJvUXjM4C\u0026amp;pg\u003dPA196\u0026amp;dq\u003dpermaboner\u0026amp;hl\u003den\u0026amp;sa\u003dX\u0026amp;ei\u003duVkMUde6JOTsiwKbyIH4DA\u0026amp;ved\u003d0CFUQ6AEwBw page 196\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"“If there were even one straight bone in this \u0027sexy as all hell\u0027 body of mine, I\u0027d have a raging \u0027\u0027permaboner\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2008\u0027\u0027, Brad Perry, \u0026quot;Hooking Up with Healthy Sexuality: The Lessons Boys Learn (and Don\u0027t Learn) About Sexuality, and Why a Sex-Positive Rape Prevention Paradigm Can Benefit Everyone Involved…\u0026quot;, in \u0027Yes Means Yes!: Visions of Female Sexual Power \u0026amp; a World Without Rape\u0027 (eds. Jaclyn Friedman \u0026amp; Jessica Valenti), Seal Press (2008), ISBN 9781580052573, [http://books.google.com/books?id\u003djkraAAAAMAAJ\u0026amp;q\u003dpermaboner\u0026amp;dq\u003dpermaboner\u0026amp; page 199\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"While I like to think I avoided the overtly harmful extremes of that mindset, I was also a chronically horny young man, and compounding my \u0027\u0027permaboner\u0027\u0027 was the fact that other dudes were playing the get-some game as intensely as they knew how\", \"priority\":5}]}, \"synonyms\":{}}");

	add("permaculture", "{\"term\":\"permaculture\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{confix|perma|culture|lang\u003dfr}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Any system of sustainable agriculture that renews natural resources and enriches local ecosystems\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The design, installation and maintenance of indefinitely sustainable human communities set in balanced ecologies, both urban and rural\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permadeath", "{\"term\":\"permadeath\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|death|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Permanent death, where the player cannot continue but is obliged to restart the game from the beginning\", \"priority\":1}]}, \"synonyms\":{}}");

	add("permafrost", "{\"term\":\"permafrost\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|frost|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Permanently frozen ground, or a specific layer thereof\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2003\u0027\u0027, Johannes C. Nonner, \u0027[http://books.google.com/books?id\u003dSQwrhRu4ZHwC Introduction to Hydrogeology]\u0027, ISBN 9026518706, page 157\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Nevertheless, the water molecule is present, as ice, in a solid form in frozen soils or \u0027\u0027permafrosts\u0027\u0027, which cover large regions characterised by a polar climate\", \"priority\":3}]}, \"synonyms\":{}}");

	add("permalink", "{\"term\":\"permalink\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|link|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A static internet address (link) to an individual blog entry or news item on the World Wide Web\", \"priority\":1}]}, \"synonyms\":{}}");

	add("permanence", "{\"term\":\"permanence\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"Latin\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027permanence\u0027 from \u0027permanentia\u0027 from Latin \u0027permaneo\u0027 (i remain; last)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state of being permanent\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The reciprocal of magnetic inductance\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permanganate", "{\"term\":\"permanganate\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{prefix|per|manganate|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"any salt of permanganic acid, they are purple crystalline solids, mostly soluble in water; they are strong oxidizing agent\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"potassium permanganate\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permansion", "{\"term\":\"permansion\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Latin\"], \"text\":\"From Latin \u0027permansio\u0027 from the participle stem of \u0027\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Permanence\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"1646\u0027\u0027, Sir Thomas Browne, \u0027Pseudodoxia Epidemica\u0027, III.17\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"from female unto male, from male to female again, and so in a circle to both, without a \u0027\u0027permansion\u0027\u0027 in either\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"Bishop John Pearso\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"Can we think that such material and mortal, that such inunderstanding souls, should be furnished with bodies of so long \u0027\u0027permansion\u0027\u0027, and our spirits joined to flesh so suddenly dissolvible, were it not that they lived but once\", \"priority\":5}]}, \"synonyms\":{}}");

	add("permatemp", "{\"term\":\"permatemp\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{blend|permanent|temp|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An employee whose official status is of a temporary, despite serving his or her function for sufficient time to be contracted as permanent\", \"priority\":1}]}, \"synonyms\":{}}");

	add("permathread", "{\"term\":\"permathread\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{prefix|perma|thread|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A permanent or perennial thread of discussion\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"2003\u0027\u0027, \u0026quot;jbritt\u0026quot;, \u0027von Rossum on Strong vs. Weak Typing\u0027 (discussion on Internet newsgroup \u0027comp.lang.ruby\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Since this is something of a \u0027\u0027permathread\u0027\u0027 on this list I though this would be of interest\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2003\u0027\u0027, \u0026quot;David Megginson\u0026quot;, \u0027Studying up on VOR\u0027s\u0027 (discussion on Internet newsgroup \u0027rec.aviation.student\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"I suspect that this is a \u0027\u0027permathread\", \"priority\":5}]}, \"synonyms\":{}}");

	add("permeability", "{\"term\":\"permeability\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"French\"], \"text\":\"From French \u0027perméabilité\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"the property of being permeabl\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"the rate of flow of a fluid through a porous materia\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"a measure of the ability of a rock to transmit fluids (such as oil or water\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"a quantitative measure of the degree of magnetization of a material in the presence of an applied magnetic field (measured in newtons per ampere squared in SI units\", \"priority\":4}]}, \"synonyms\":{}}");

	add("permeance", "{\"term\":\"permeance\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"Latin\"], \"text\":\"From stem of Latin \u0027lang\u003dla|permere\u0027 () {{suffix||ance|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A measure of the degree to which a material allows a fluid to permeate it\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The reciprocal of reluctance in a magnetic circuit; the analogue of conductance in an electrical circuit\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permian", "{\"term\":\"permian\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"From {{suffix|Perm|ian|lang\u003den}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"An inhabitant of the Russian region of Perm; specifically, one belonging to a branch of the Finno-Ugric peoples, including Komis and Udmurts, who speak Permic languages\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The language of such people; Permic\", \"priority\":2}]}, \"synonyms\":{}}");

