	add("permission", "{\"term\":\"permission\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"Latin\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027permission\u0027 from Latin \u0027permissio\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"authorisation; consent (especially formal consent from someone in authority\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Sire, do I have your \u0027\u0027permission\u0027\u0027 to execute this traitor\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"The act of permitting\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"flags or access control lists pertaining to a file that dictate who can access it, and how\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"I used the \u0026quot;chmod\u0026quot; command to change the file\u0027s \u0027\u0027permission\", \"priority\":5}]}, \"synonyms\":{}}");

	add("permissiveness", "{\"term\":\"permissiveness\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The relative likelihood of something or someone to grant permission or allow something to happen\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The \u0027\u0027permissiveness\u0027\u0027 of campus life leads many college freshmen to try things they would never have experienced at home\", \"priority\":2}]}, \"synonyms\":{}}");

	add("permutation", "{\"term\":\"permutation\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"old french (842-ca. 1400)\"], \"text\":\"From Old french (842-ca. 1400) \u0027permutacion\u0027 (French:. French|permutation\u0027).\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One of the ways something exists, or the ways a set of objects can be ordered\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Which \u0027\u0027permutation\u0027\u0027 for completing our agenda items makes the most sense\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A one-to-one mapping from a finite set to itself\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"This \u0027\u0027permutation\u0027\u0027 takes each element to the one following it, with the last mapped back to the first\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"An ordering of a finite set of distinct elements\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"There are six \u0027\u0027permutations\u0027\u0027 of three elements, e.g. {abc, acb, bac, bca, cab, cba\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"A transformation of a set\u0027s prime form, by applying one or more of certain operations, specifically, transposition, inversion, and retrograde\", \"priority\":7}]}, \"synonyms\":{}}");

	add("permuter", "{\"term\":\"permuter\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"{{suffix|permute|er|lang\u003den}}\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"One who permutes\", \"priority\":1}]}, \"synonyms\":{}}");

