	add("perplexity", "{\"term\":\"perplexity\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\"], \"text\":\"From Middle french (ca. 1400-1600) \u0027perplexité\u0027 or post-classical Latin \u0027perplexitas\u0027 ‘entanglement’, from \u0027perplexus\u0027.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The state or quality of being perplexed; puzzled or confused\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Something that perplexes\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"1942\u0027\u0027, Rebecca West, \u0027Black Lamb and Grey Falcon\u0027 (Canongate 2006), page 149\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"The Emperor, who was by then a focus of unresolvable \u0027\u0027perplexities\u0027\u0027, stood providing a strongly contrary appearance\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A measurement in information theory: see\", \"priority\":5}]}, \"synonyms\":{}}");

	add("perquisite", "{\"term\":\"perquisite\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\"], \"text\":\"From \u0027perquisitum\u0027 (something acquired for profit).\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Any monetary or other incidental benefit beyond salary\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The perquisites of this job include health insurance and a performance bonus\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A gratuity\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"After the wonderful service that evening he didn’t hesitate in laying a substantial perquisite on the table\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A privilege or possession held or claimed exclusively by a certain person, group or class\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"Private jets and motor yachts are a perquisite of the rich\", \"priority\":6}]}, \"synonyms\":{}}");

	add("perry", "{\"term\":\"perry\", \"etymology\":{\"influencers\":[], \"languages\":[], \"text\":\"\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"derived from the \u0027pyrige\u0027 (a pear tree\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"derived from the surname, or a pet form of the rare given name Peregrine\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A town in Arkansas\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"A village in Cambridgeshire, Englan\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"A city in Florida\", \"priority\":5},{\"upperType\":\"NOUN\", \"text\":\"A city in Georgia, USA\", \"priority\":6},{\"upperType\":\"NOUN\", \"text\":\"A village in Illinois\", \"priority\":7},{\"upperType\":\"NOUN\", \"text\":\"A city in Iowa\", \"priority\":8},{\"upperType\":\"NOUN\", \"text\":\"A city in Kansas\", \"priority\":9},{\"upperType\":\"NOUN\", \"text\":\"A town in Maine\", \"priority\":10},{\"upperType\":\"NOUN\", \"text\":\"A city in Michigan\", \"priority\":11},{\"upperType\":\"NOUN\", \"text\":\"A city in Missouri\", \"priority\":12},{\"upperType\":\"NOUN\", \"text\":\"A village in Ohio\", \"priority\":13},{\"upperType\":\"NOUN\", \"text\":\"A city in Oklahoma\", \"priority\":14},{\"upperType\":\"NOUN\", \"text\":\"A township in Ontario, Canad\", \"priority\":15},{\"upperType\":\"NOUN\", \"text\":\"A city in Utah\", \"priority\":16}]}, \"synonyms\":{}}");

	add("perry", "{\"term\":\"perry\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle english (1100-1500)\", \"middle french (ca. 1400-1600)\", \"Latin\"], \"text\":\"From Middle english (1100-1500) \u0027peirrie\u0027; from Middle french (ca. 1400-1600) \u0027peré\u0027; from (assumed) \u0027piratum\u0027; from Latin \u0027pirum\u0027.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A fermented alcoholic beverage made from pears; somewhat analogous to cider\", \"priority\":1}]}, \"synonyms\":{}}");

	add("perron", "{\"term\":\"perron\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle french (ca. 1400-1600)\", \"anglo-norman\"], \"text\":\"From Anglo-norman and Middle french (ca. 1400-1600) \u0027perron\u0027 from \u0027pierre\u0027 ()\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A stone block used as the base of a monument, marker, etc\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"hit wille be no worship for you to haue adoo with me / for ye are fressh and I am wounded sore / And therfor and ye wille nedes haue ado with me / Assigne me a day and thenne I shal mete with you withoute fayle / ye saye wel said sir Tristram / Now I assigne you to mete me in the medowe by the ryuer of Camelot / where Merlyon sette the \u0027\u0027peron\u0027\u0027\u0026lt;!--Prior quote:Now I assygne you to mete me in the medowe by the river of Camelot, where Merlyon sette the \u0027\u0027perowne\u0027\u0027.--\u0026gt\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"A platform outside the raised entrance to a church or large building, or the steps leading to such a platform\", \"priority\":3}]}, \"synonyms\":{}}");

	add("persecution", "{\"term\":\"persecution\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"old french (842-ca. 1400)\", \"Latin\"], \"text\":\"From Old french (842-ca. 1400) \u0027persecucion\u0027 from \u0027persecūtio\u0027 (chase, pursuit) from Latin \u0027persequor\u0027 (follow up, pursue) from {{prefix|per|sequor|t1\u003dthrough|t2\u003dfollow|lang\u003dla|nocat\u003d1}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The act of persecuting\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A program or campaign to subjugate or eliminate a specific group of people, often based on race, religion, sexuality, or social beliefs\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"title\u003dRace volume\u003d100|issue\u003d2|page\u003d164|magazine\u003d|url\u003dhttp://www.americanscientist.org/bookshelf/pub/race-finished|passage\u003dFew concepts are as emotionally charged as that of race. The word conjures up a mixture of associations—culture, ethnicity, genetics, subjugation, exclusion and \u0027\u0027persecution\u0027\u0027. But is the tragic history of efforts to define groups of people by race really a matter of the misuse of science, the abuse of a valid biological concept\", \"priority\":3}]}, \"synonyms\":{}}");

	add("persephone", "{\"term\":\"persephone\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\"], \"text\":\"From Ancient greek (to 1453) \u0027περσεφόνη\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"thumb|right|230px|Triptolemus and Persephone (1\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"A minor deity, the queen of the Underworld/Hades, and goddess of the seasons and vegetation. Originally named Kore/Core, she is the daughter of Zeus and Demeter; and the wife of Hades. Her Roman counterpart is Proserpina\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Short for 399 Persephone, a main belt asteroid\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"2008\u0027\u0027, Gaynor McGrath, \u0027Lemniscate\u0027, Transit Lounge Publishing (2011), ISBN 9781921924149 (e-book), chapter 23\", \"priority\":4},{\"upperType\":\"NOUN\", \"text\":\"‘My name is \u0027\u0027Persephone\u0027\u0027’ she says, ‘but in England everyone calls me Seph.’\", \"priority\":5}]}, \"synonyms\":{}}");

	add("persepolis", "{\"term\":\"persepolis\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\"], \"text\":\"From Latin {{m|la|Persepolis}}, from Ancient greek (to 1453) {{m|grc|Περσέπολις}}, from {{m|grc|Πέρσης||Persian}} + {{m|grc|πόλις||city}}.\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"The ceremonial capital city of Achaemenid Persia\", \"priority\":1}]}, \"synonyms\":{}}");

	add("persia", "{\"term\":\"persia\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"ancient greek (to 1453)\", \"Latin\", \"old persian (ca. 600-400 b.c.)\"], \"text\":\"From Latin \u0027persia\u0027 from Ancient greek (to 1453) \u0027περσίς\u0027 from Old persian (ca. 600-400 b.c.) \u0027𐎱𐎠𐎼𐎿\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Territories corresponding to either of the two Persian Empires ruled by dynasties from Persis, specially the first of the two\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"Former name of Iran\", \"priority\":2}]}, \"synonyms\":{}}");

	add("persian", "{\"term\":\"persian\", \"etymology\":{\"influencers\":[], \"languages\":[\"English\", \"middle english (1100-1500)\", \"middle french (ca. 1400-1600)\", \"ancient greek (to 1453)\", \"Italian\", \"Latin\", \"old persian (ca. 600-400 b.c.)\"], \"text\":\"From Middle english (1100-1500) {{m|enm|percynne}}, from Middle french (ca. 1400-1600) {{m|frm|persien}}, from Italian {{m|it|persiano}}, from {{m|la|Persiānus}}, from Latin {{m|la|Persia}}, from Ancient greek (to 1453) {{m|grc|Περσίς}}, from Old persian (ca. 600-400 b.c.) {{m|peo|𐎱𐎠𐎼𐎿||Persia|tr\u003dPārsa}} (compare modern Persian {{m|fa|sc\u003dfa-Arab|پارس|tr\u003dPārs}}).\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A family of languages spoken primarily in Iran, Afghanistan, Uzbekistan, and Tajikistan\", \"priority\":1}]}, \"synonyms\":{}}");

	add("persiflage", "{\"term\":\"persiflage\", \"etymology\":{\"influencers\":[{},{}], \"languages\":[\"English\", \"French\", \"Latin\"], \"text\":\"From French from \u0027persifler\u0027 (to quiz) from \u0027per-\u0027 + \u0027siffler\u0027 (to whistle) from Latin \u0027sībilō\u0027 (whistle)\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"Good-natured banter; raillery\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"After the third strike he returned to the bench to face the inevitable persiflage from his teammates\", \"priority\":2},{\"upperType\":\"NOUN\", \"text\":\"Frivolous, lighthearted discussion of a topic\", \"priority\":3},{\"upperType\":\"NOUN\", \"text\":\"Polite dinner calls for persiflage rather than in-depth possibly offensive discussion\", \"priority\":4}]}, \"synonyms\":{}}");

	add("persimmon", "{\"term\":\"persimmon\", \"etymology\":{\"influencers\":[{}], \"languages\":[\"English\", \"powhatan\", \"unami\"], \"text\":\"From Powhatan \u0027pichamins\u0027 \u0027pushemins\u0027 \u0027pasimenan\u0027 (dried fruit) from the same Proto-algonquian root {{m|alg-pro|*mini|*-imin|fruit, berry}} as Unami \u0027ximin\u0027\"}, \"definitions\":{\"list\":[{\"upperType\":\"NOUN\", \"text\":\"A type of fruit, of orange colour, very sweet, quite astringent when immature\", \"priority\":1},{\"upperType\":\"NOUN\", \"text\":\"The tree this fruit grows on, generally one of two species of ebony: (Asian) or (North American\", \"priority\":2}]}, \"synonyms\":{}}");

