package org.swtk.commons.dict.wordnet.indexbyname.controller.i.k;  import java.util.ArrayList; import java.util.Collection; import java.util.Set; import java.util.TreeSet;  import org.swtk.common.dict.dto.wordnet.IndexNoun;  	import org.swtk.commons.dict.wordnet.indexbyname.instance.i.k.h.WordnetNounIndexNameInstanceIKH;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.i.k.o.WordnetNounIndexNameInstanceIKO;
  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerIK {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException { 		if (TERM.length() < 3) throw new BusinessException("TERM not found (term = %s)", TERM); 		 		String key = TERM.substring(0, 3).toLowerCase(); 			if ("ikh".equals(key)) return WordnetNounIndexNameInstanceIKH.get(TERM);
	if ("iko".equals(key)) return WordnetNounIndexNameInstanceIKO.get(TERM);
  		throw new BusinessException("TERM not found (term = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceIKH.terms());
	set.addAll(WordnetNounIndexNameInstanceIKO.terms());
  		return set; 	} }