package org.swtk.commons.dict.wordnet.indexbyname.controller.k.h;  import java.util.Set; import java.util.TreeSet;  import org.swtk.common.dict.dto.wordnet.IndexNoun;  	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.a.WordnetNounIndexNameInstanceKHA;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.e.WordnetNounIndexNameInstanceKHE;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.i.WordnetNounIndexNameInstanceKHI;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.m.WordnetNounIndexNameInstanceKHM;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.o.WordnetNounIndexNameInstanceKHO;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.r.WordnetNounIndexNameInstanceKHR;
	import org.swtk.commons.dict.wordnet.indexbyname.instance.k.h.u.WordnetNounIndexNameInstanceKHU;
  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerKH {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException { 		if (TERM.length() < 3) throw new BusinessException("TERM not found (term = %s)", TERM); 		 		String key = TERM.substring(0, 3).toLowerCase(); 			if ("kha".equals(key)) return WordnetNounIndexNameInstanceKHA.get(TERM);
	if ("khe".equals(key)) return WordnetNounIndexNameInstanceKHE.get(TERM);
	if ("khi".equals(key)) return WordnetNounIndexNameInstanceKHI.get(TERM);
	if ("khm".equals(key)) return WordnetNounIndexNameInstanceKHM.get(TERM);
	if ("kho".equals(key)) return WordnetNounIndexNameInstanceKHO.get(TERM);
	if ("khr".equals(key)) return WordnetNounIndexNameInstanceKHR.get(TERM);
	if ("khu".equals(key)) return WordnetNounIndexNameInstanceKHU.get(TERM);
  		throw new BusinessException("TERM not found (term = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceKHA.terms());
	set.addAll(WordnetNounIndexNameInstanceKHE.terms());
	set.addAll(WordnetNounIndexNameInstanceKHI.terms());
	set.addAll(WordnetNounIndexNameInstanceKHM.terms());
	set.addAll(WordnetNounIndexNameInstanceKHO.terms());
	set.addAll(WordnetNounIndexNameInstanceKHR.terms());
	set.addAll(WordnetNounIndexNameInstanceKHU.terms());
  		return set; 	} }