  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerIB {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException {  		String key = TERM.substring(0, 4).toLowerCase(); 			if ("ibad".equals(key)) return WordnetNounIndexNameInstanceIBAD.get(TERM);
	if ("iber".equals(key)) return WordnetNounIndexNameInstanceIBER.get(TERM);
	if ("ibex".equals(key)) return WordnetNounIndexNameInstanceIBEX.get(TERM);
	if ("ibis".equals(key)) return WordnetNounIndexNameInstanceIBIS.get(TERM);
	if ("ibra".equals(key)) return WordnetNounIndexNameInstanceIBRA.get(TERM);
	if ("ibrd".equals(key)) return WordnetNounIndexNameInstanceIBRD.get(TERM);
	if ("ibse".equals(key)) return WordnetNounIndexNameInstanceIBSE.get(TERM);
	if ("ibup".equals(key)) return WordnetNounIndexNameInstanceIBUP.get(TERM);
  		throw new BusinessException("TERM not found (id = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceIBAD.terms());
	set.addAll(WordnetNounIndexNameInstanceIBER.terms());
	set.addAll(WordnetNounIndexNameInstanceIBEX.terms());
	set.addAll(WordnetNounIndexNameInstanceIBIS.terms());
	set.addAll(WordnetNounIndexNameInstanceIBRA.terms());
	set.addAll(WordnetNounIndexNameInstanceIBRD.terms());
	set.addAll(WordnetNounIndexNameInstanceIBSE.terms());
	set.addAll(WordnetNounIndexNameInstanceIBUP.terms());
  		return set; 	} }