  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerIP {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException {  		String key = TERM.substring(0, 4).toLowerCase(); 			if ("ipec".equals(key)) return WordnetNounIndexNameInstanceIPEC.get(TERM);
	if ("iphi".equals(key)) return WordnetNounIndexNameInstanceIPHI.get(TERM);
	if ("ipid".equals(key)) return WordnetNounIndexNameInstanceIPID.get(TERM);
	if ("ipod".equals(key)) return WordnetNounIndexNameInstanceIPOD.get(TERM);
	if ("ipom".equals(key)) return WordnetNounIndexNameInstanceIPOM.get(TERM);
	if ("ipro".equals(key)) return WordnetNounIndexNameInstanceIPRO.get(TERM);
	if ("ipse".equals(key)) return WordnetNounIndexNameInstanceIPSE.get(TERM);
	if ("ipsu".equals(key)) return WordnetNounIndexNameInstanceIPSU.get(TERM);
  		throw new BusinessException("TERM not found (id = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceIPEC.terms());
	set.addAll(WordnetNounIndexNameInstanceIPHI.terms());
	set.addAll(WordnetNounIndexNameInstanceIPID.terms());
	set.addAll(WordnetNounIndexNameInstanceIPOD.terms());
	set.addAll(WordnetNounIndexNameInstanceIPOM.terms());
	set.addAll(WordnetNounIndexNameInstanceIPRO.terms());
	set.addAll(WordnetNounIndexNameInstanceIPSE.terms());
	set.addAll(WordnetNounIndexNameInstanceIPSU.terms());
  		return set; 	} }