  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerOF {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException {  		String key = TERM.substring(0, 4).toLowerCase(); 			if ("offa".equals(key)) return WordnetNounIndexNameInstanceOFFA.get(TERM);
	if ("offb".equals(key)) return WordnetNounIndexNameInstanceOFFB.get(TERM);
	if ("offe".equals(key)) return WordnetNounIndexNameInstanceOFFE.get(TERM);
	if ("offi".equals(key)) return WordnetNounIndexNameInstanceOFFI.get(TERM);
	if ("offp".equals(key)) return WordnetNounIndexNameInstanceOFFP.get(TERM);
	if ("offs".equals(key)) return WordnetNounIndexNameInstanceOFFS.get(TERM);
	if ("ofte".equals(key)) return WordnetNounIndexNameInstanceOFTE.get(TERM);
  		throw new BusinessException("TERM not found (id = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceOFFA.terms());
	set.addAll(WordnetNounIndexNameInstanceOFFB.terms());
	set.addAll(WordnetNounIndexNameInstanceOFFE.terms());
	set.addAll(WordnetNounIndexNameInstanceOFFI.terms());
	set.addAll(WordnetNounIndexNameInstanceOFFP.terms());
	set.addAll(WordnetNounIndexNameInstanceOFFS.terms());
	set.addAll(WordnetNounIndexNameInstanceOFTE.terms());
  		return set; 	} }