package org.swtk.commons.dict.wordnet.index.name.controller.w.u;  import java.util.Collection; import java.util.Set; import java.util.TreeSet;  import org.swtk.common.dict.dto.wordnet.IndexNoun;  	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.e.r.WordnetNounIndexNameInstanceWUER;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.h.a.WordnetNounIndexNameInstanceWUHA;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.l.f.WordnetNounIndexNameInstanceWULF;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.r.l.WordnetNounIndexNameInstanceWURL;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.r.t.WordnetNounIndexNameInstanceWURT;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.r.z.WordnetNounIndexNameInstanceWURZ;
	import org.swtk.commons.dict.wordnet.index.name.instance.w.u.s.s.WordnetNounIndexNameInstanceWUSS;
  import com.trimc.blogger.commons.exception.BusinessException;  public final class WordnetNounIndexNameControllerWU {  	public static Collection<IndexNoun> get(final String TERM) throws BusinessException {  		String key = TERM.substring(0, 4).toLowerCase(); 			if ("wuer".equals(key)) return WordnetNounIndexNameInstanceWUER.get(TERM);
	if ("wuha".equals(key)) return WordnetNounIndexNameInstanceWUHA.get(TERM);
	if ("wulf".equals(key)) return WordnetNounIndexNameInstanceWULF.get(TERM);
	if ("wurl".equals(key)) return WordnetNounIndexNameInstanceWURL.get(TERM);
	if ("wurt".equals(key)) return WordnetNounIndexNameInstanceWURT.get(TERM);
	if ("wurz".equals(key)) return WordnetNounIndexNameInstanceWURZ.get(TERM);
	if ("wuss".equals(key)) return WordnetNounIndexNameInstanceWUSS.get(TERM);
  		throw new BusinessException("TERM not found (id = %s)", TERM); 	} 	 	public static Collection<String> terms() throws BusinessException { 		Set<String> set = new TreeSet<String>();  			set.addAll(WordnetNounIndexNameInstanceWUER.terms());
	set.addAll(WordnetNounIndexNameInstanceWUHA.terms());
	set.addAll(WordnetNounIndexNameInstanceWULF.terms());
	set.addAll(WordnetNounIndexNameInstanceWURL.terms());
	set.addAll(WordnetNounIndexNameInstanceWURT.terms());
	set.addAll(WordnetNounIndexNameInstanceWURZ.terms());
	set.addAll(WordnetNounIndexNameInstanceWUSS.terms());
  		return set; 	} }